import React, { useState, useEffect } from 'react';
import Bubble from './components/Bubble';

interface BubbleData {
    id: number;
    position: { x: number; y: number };
}

const UI_WIDTH = 260; // width of your UI panel
const UI_HEIGHT = 140; // height of your UI panel

const getRandomPosition = () => ({
    x: Math.random() * (window.innerWidth - 80) + UI_WIDTH,
    y: Math.random() * (window.innerHeight - 120) + UI_HEIGHT,
});

const UPGRADE_COST = 50;

const STORAGE_KEY = 'bubble-clicker-game-state';

const App: React.FC = () => {
    const [bubbles, setBubbles] = useState<BubbleData[]>([]);
    const [currency, setCurrency] = useState(0);
    const [upgradeLevel, setUpgradeLevel] = useState(0);
    const [goldPerSecond, setGoldPerSecond] = useState(0);

    // Load game state from localStorage on mount
    useEffect(() => {
        const saved = localStorage.getItem(STORAGE_KEY);
        if (saved) {
            try {
                const state = JSON.parse(saved);
                setCurrency(state.currency ?? 0);
                setUpgradeLevel(state.upgradeLevel ?? 0);
                setGoldPerSecond(state.goldPerSecond ?? 0);
            } catch (e) {
                // Ignore parse errors
            }
        }
    }, []);

    // Save game state to localStorage every 5 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            localStorage.setItem(
                STORAGE_KEY,
                JSON.stringify({
                    currency,
                    upgradeLevel,
                    goldPerSecond,
                })
            );
        }, 5000);
        return () => clearInterval(interval);
    }, [currency, upgradeLevel, goldPerSecond]);

    // Spawn a bubble every second
    useEffect(() => {
        const interval = setInterval(() => {
            setBubbles(bubs => [
                ...bubs,
                { id: Date.now() + Math.random(), position: getRandomPosition() }
            ]);
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    // Gold per second effect
    useEffect(() => {
        if (goldPerSecond > 0) {
            const interval = setInterval(() => {
                setCurrency(c => c + goldPerSecond);
            }, 1000);
            return () => clearInterval(interval);
        }
    }, [goldPerSecond]);

    const handlePop = (id: number) => {
        setBubbles(bubs => bubs.filter(b => b.id !== id));
        setCurrency(c => c + 1);
    };

    const handleUpgrade = () => {
        if (currency >= UPGRADE_COST) {
            setCurrency(c => c - UPGRADE_COST);
            setUpgradeLevel(lvl => lvl + 1);
            setGoldPerSecond(gps => gps + 1);
        }
    };

    return (
        <div style={{ position: 'relative', width: '100vw', height: '100vh', overflow: 'hidden', background: 'linear-gradient(to bottom, #e0f7fa, #ffffff 80%)' }}>
            <div style={{
                position: 'absolute',
                top: 20,
                left: 20,
                zIndex: 10,
                background: 'rgba(255,255,255,0.7)',
                borderRadius: 12,
                padding: '8px 20px',
                fontSize: 24,
                fontWeight: 600,
                color: '#0288d1',
                boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
            }}>
                ðŸ’° {currency}
            </div>
            <div style={{
                position: 'absolute',
                top: 70,
                left: 20,
                zIndex: 10,
                background: 'rgba(255,255,255,0.8)',
                borderRadius: 12,
                padding: '12px 20px',
                fontSize: 18,
                color: '#0288d1',
                boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
            }}>
                <div>Upgrade Level: {upgradeLevel}</div>
                <div>Gold per second: {goldPerSecond}</div>
                <button
                    onClick={handleUpgrade}
                    disabled={currency < UPGRADE_COST}
                    style={{
                        marginTop: 8,
                        padding: '8px 16px',
                        fontSize: 16,
                        borderRadius: 8,
                        border: 'none',
                        background: currency >= UPGRADE_COST ? '#4fc3f7' : '#b0bec5',
                        color: '#fff',
                        cursor: currency >= UPGRADE_COST ? 'pointer' : 'not-allowed'
                    }}
                >
                    Buy Upgrade ({UPGRADE_COST} gold)
                </button>
            </div>
            {bubbles.map(bubble => (
                <Bubble
                    key={bubble.id}
                    position={bubble.position}
                    onPop={() => handlePop(bubble.id)}
                />
            ))}
        </div>
    );
};

export default App;
