import React, { useState } from 'react';

interface BubbleProps {
    position: { x: number; y: number };
    onPop: () => void;
}

const Bubble: React.FC<BubbleProps> = ({ position, onPop }) => {
    const [popping, setPopping] = useState(false);

    const handleClick = () => {
        setPopping(true);
        setTimeout(() => {
            onPop();
        }, 200); // Duration matches animation
    };

    const bubbleStyle: React.CSSProperties = {
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '50px',
        height: '50px',
        borderRadius: '50%',
        backgroundColor: 'lightblue',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '20px',
        color: 'white',
        transition: 'transform 0.2s, opacity 0.2s',
        transform: popping ? 'scale(1.5)' : 'scale(1)',
        opacity: popping ? 0 : 1,
        zIndex: 5,
        userSelect: 'none',
    };

    return (
        <div style={bubbleStyle} onClick={handleClick}>
            ðŸ’§
        </div>
    );
};

export default Bubble;
