import React, { useState, useEffect } from 'react';
import Bubble from './components/Bubble';

interface BubbleData {
    id: number;
    position: { x: number; y: number };
}

const getRandomPosition = () => ({
    x: Math.random() * (window.innerWidth - 60),
    y: Math.random() * (window.innerHeight - 120),
});

const App: React.FC = () => {
    const [bubbles, setBubbles] = useState<BubbleData[]>([]);
    const [currency, setCurrency] = useState(0);

    // Spawn a bubble every second
    useEffect(() => {
        const interval = setInterval(() => {
            setBubbles(bubs => [
                ...bubs,
                { id: Date.now() + Math.random(), position: getRandomPosition() }
            ]);
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    const handlePop = (id: number) => {
        setBubbles(bubs => bubs.filter(b => b.id !== id));
        setCurrency(c => c + 1);
    };

    return (
        <div style={{ position: 'relative', width: '100vw', height: '100vh', overflow: 'hidden' }}>
            <h2 style={{ position: 'absolute', top: 10, left: 10, zIndex: 10 }}>ðŸ’° {currency}</h2>
            {bubbles.map(bubble => (
                <Bubble
                    key={bubble.id}
                    position={bubble.position}
                    onPop={() => handlePop(bubble.id)}
                />
            ))}
        </div>
    );
};

export default App;
