import React, { useMemo, useState } from "react";

interface BubbleProps {
    position: { x: number; y: number };
    onPop: () => void;
}

const Bubble: React.FC<BubbleProps> = ({ position, onPop }) => {
    const [popped, setPopped] = useState(false);

    // Randomize size and float duration
    const { size, floatDuration, floatDistance } = useMemo(() => {
        const size = 48 + Math.random() * 32;
        const floatDuration = 3 + Math.random() * 2; // 3-5s
        const floatDistance = 180 + Math.random() * 120; // px
        return { size, floatDuration, floatDistance };
    }, []);

    const handlePop = () => {
        setPopped(true);
        setTimeout(onPop, 400);
    };

    // Start bubbles near the bottom
    const left = position.x;
    const bottom = 40 + Math.random() * 40; // px from bottom

    return (
        <div
            className="absolute cursor-pointer select-none"
            style={{
                left,
                bottom,
                width: size,
                height: size,
                zIndex: 10,
                pointerEvents: popped ? "none" : "auto",
                animation: !popped
                    ? `bubble-float-up ${floatDuration}s linear forwards`
                    : undefined,
            }}
            onClick={handlePop}
        >
            <div className={`bubble-visual ${popped ? "bubble-pop" : ""}`}>
                <div className="bubble-highlight" />
                {/* Splash droplets if you want */}
            </div>
        </div>
    );
};

export default Bubble;
