import React, { useState } from 'react';

interface BubbleProps {
    position: { x: number; y: number };
    onPop: () => void;
}

const Bubble: React.FC<BubbleProps> = ({ position, onPop }) => {
    const [popping, setPopping] = useState(false);

    const handleClick = () => {
        setPopping(true);
        setTimeout(() => {
            onPop();
        }, 200); // Duration matches animation
    };

    const bubbleStyle: React.CSSProperties = {
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '60px',
        height: '60px',
        borderRadius: '50%',
        background: 'radial-gradient(circle at 20% 20%, #fff 40%, #aeeaff 60%, #6ec6ff 100%)',
        boxShadow: '0 0 16px 4px #b3e5fc, 0 0 32px 8px #e1f5fe',
        border: '2px solid #e0f7fa',
        opacity: popping ? 0 : 0.85,
        transform: popping ? 'scale(1.5)' : 'scale(1)',
        transition: 'transform 0.2s, opacity 0.2s',
        cursor: 'pointer',
        zIndex: 5,
        userSelect: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    };

    // Optional: Add a highlight for a more "soapy" look
    const highlightStyle: React.CSSProperties = {
        position: 'absolute',
        top: '12px',
        left: '18px',
        width: '16px',
        height: '8px',
        borderRadius: '50%',
        background: 'rgba(255,255,255,0.7)',
        filter: 'blur(1px)',
        pointerEvents: 'none',
    };

    return (
        <div style={bubbleStyle} onClick={handleClick}>
            <div style={highlightStyle}></div>
        </div>
    );
};

export default Bubble;
