import React, { useState, useEffect } from 'react';
import Bubble from './components/Bubble';

interface BubbleData {
    id: number;
    position: { x: number; y: number };
}

const UI_WIDTH = 260;
const UI_HEIGHT = 140;
const UPGRADE_COST = 50;
const STORAGE_KEY = 'bubble-clicker-game-state';

const getRandomPosition = () => {
    // UI panel is at left: 20, top: 20, width: 260, height: 140
    const minX = UI_WIDTH + 40; // 260 + 40 = 300, so bubbles start to the right of UI
    const minY = 0;
    const maxX = window.innerWidth - 80;
    const maxY = window.innerHeight - 120;
    return {
        x: Math.random() * (maxX - minX) + minX,
        y: Math.random() * (maxY - minY) + minY,
    };
};

const getInitialState = () => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
        try {
            const state = JSON.parse(saved);
            return {
                currency: state.currency ?? 0,
                upgradeLevel: state.upgradeLevel ?? 0,
            };
        } catch {
            // ignore
        }
    }
    return { currency: 0, upgradeLevel: 0 };
};

const initialState = getInitialState();
console.log('Loaded state:', initialState);

const App: React.FC = () => {
    const [bubbles, setBubbles] = useState<BubbleData[]>([]);
    const [currency, setCurrency] = useState(initialState.currency);
    const [upgradeLevel, setUpgradeLevel] = useState(initialState.upgradeLevel);
    const [shopOpen, setShopOpen] = useState(false);

    const goldPerSecond = upgradeLevel;

    // Save game state to localStorage every 5 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            const data = { currency, upgradeLevel };
            console.log('Saving state:', data);
            localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
        }, 5000);
        return () => clearInterval(interval);
    }, [currency, upgradeLevel]);

    // Spawn a bubble every second
    useEffect(() => {
        const interval = setInterval(() => {
            setBubbles(bubs => [
                ...bubs,
                { id: Date.now() + Math.random(), position: getRandomPosition() }
            ]);
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    // Gold per second effect
    useEffect(() => {
        if (goldPerSecond > 0) {
            const interval = setInterval(() => {
                setCurrency(c => c + goldPerSecond);
            }, 1000);
            return () => clearInterval(interval);
        }
    }, [goldPerSecond]);

    const handlePop = (id: number) => {
        setBubbles(bubs => bubs.filter(b => b.id !== id));
        setCurrency(c => c + 1);
    };

    const handleUpgrade = () => {
        if (currency >= UPGRADE_COST) {
            setCurrency(c => c - UPGRADE_COST);
            setUpgradeLevel(lvl => lvl + 1);
        }
    };

    // Example shop items
    const shopItems = [
        { id: 1, name: 'Bubble Blaster', cost: 100 },
        { id: 2, name: 'Auto Popper', cost: 250 },
        { id: 3, name: 'Golden Soap', cost: 500 },
    ];

    const handleBuy = (item: { id: number; name: string; cost: number }) => {
        if (currency >= item.cost) {
            setCurrency(c => c - item.cost);
            // Add your item effect logic here
            alert(`You bought: ${item.name}`);
        }
    };

    return (
        <div style={{ position: 'relative', width: '100vw', height: '100vh', overflow: 'hidden', background: 'linear-gradient(to bottom, #e0f7fa, #ffffff 80%)' }}>
            {/* Shop Icon Button */}
            <button
                className="fixed top-6 left-6 z-30 bg-white/90 rounded-full shadow-lg p-3 text-2xl hover:bg-blue-100 transition"
                onClick={() => setShopOpen(open => !open)}
                aria-label="Open Shop"
            >
                🛒
            </button>

            {/* Side Shop Menu */}
            <div
                className={`fixed top-0 left-0 h-full w-80 bg-white/90 shadow-2xl z-40 transform transition-transform duration-300 ${
                    shopOpen ? 'translate-x-0' : '-translate-x-full'
                }`}
            >
                <div className="flex items-center justify-between px-6 py-4 border-b">
                    <h3 className="text-xl font-bold text-blue-700">Shop</h3>
                    <button
                        className="text-2xl font-bold text-gray-500 hover:text-blue-500"
                        onClick={() => setShopOpen(false)}
                        aria-label="Close Shop"
                    >
                        ×
                    </button>
                </div>
                <ul className="p-6">
                    {shopItems.map(item => (
                        <li key={item.id} className="flex items-center justify-between mb-4">
                            <span className="font-medium">{item.name}</span>
                            <span className="mx-2 text-yellow-700 font-semibold">{item.cost}💰</span>
                            <button
                                className="px-3 py-1 rounded bg-blue-400 text-white font-semibold disabled:bg-gray-300 disabled:cursor-not-allowed"
                                disabled={currency < item.cost}
                                onClick={() => handleBuy(item)}
                            >
                                Buy
                            </button>
                        </li>
                    ))}
                </ul>
            </div>

            <div style={{
                position: 'absolute',
                top: 20,
                left: 20,
                zIndex: 10,
                background: 'rgba(255,255,255,0.7)',
                borderRadius: 12,
                padding: '8px 20px',
                fontSize: 24,
                fontWeight: 600,
                color: '#0288d1',
                boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
            }}>
                💰 {currency}
            </div>
            <div style={{
                position: 'absolute',
                top: 70,
                left: 20,
                zIndex: 10,
                background: 'rgba(255,255,255,0.8)',
                borderRadius: 12,
                padding: '12px 20px',
                fontSize: 18,
                color: '#0288d1',
                boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
            }}>
                <div>Upgrade Level: {upgradeLevel}</div>
                <div>Gold per second: {goldPerSecond}</div>
                <button
                    onClick={handleUpgrade}
                    disabled={currency < UPGRADE_COST}
                    style={{
                        marginTop: 8,
                        padding: '8px 16px',
                        fontSize: 16,
                        borderRadius: 8,
                        border: 'none',
                        background: currency >= UPGRADE_COST ? '#4fc3f7' : '#b0bec5',
                        color: '#fff',
                        cursor: currency >= UPGRADE_COST ? 'pointer' : 'not-allowed'
                    }}
                >
                    Buy Upgrade ({UPGRADE_COST} gold)
                </button>
            </div>
            {bubbles.map(bubble => (
                <Bubble
                    key={bubble.id}
                    position={bubble.position}
                    onPop={() => handlePop(bubble.id)}
                />
            ))}
        </div>
    );
};

export default App;
