import React, { useMemo, useState } from "react";

const SPLASH_DIRECTIONS = [
    { x: 0, y: -1 },
    { x: 0.87, y: -0.5 },
    { x: 0.87, y: 0.5 },
    { x: 0, y: 1 },
    { x: -0.87, y: 0.5 },
    { x: -0.87, y: -0.5 },
];

interface BubbleProps {
    position: { x: number; y: number };
    onPop: () => void;
}

const Bubble: React.FC<BubbleProps> = ({ position, onPop }) => {
    // Randomize size and animation duration for each bubble
    const { size, animationDuration } = useMemo(() => {
        const size = 48 + Math.random() * 32; // 48px to 80px
        const animationDuration = 2 + Math.random() * 1.5; // 2s to 3.5s
        return { size, animationDuration };
    }, []);

    // Pop state for splash animation
    const [popped, setPopped] = useState(false);

    const handlePop = () => {
        setPopped(true);
        setTimeout(onPop, 400); // Wait for splash animation
    };

    return (
        <div
            className="absolute cursor-pointer select-none"
            style={{
                left: position.x,
                top: position.y,
                width: size,
                height: size,
                zIndex: 10,
                pointerEvents: popped ? "none" : "auto",
            }}
            onClick={handlePop}
        >
            <div
                className={`bubble-visual ${popped ? "bubble-pop" : ""}`}
                style={{
                    width: "100%",
                    height: "100%",
                    borderRadius: "50%",
                    boxShadow: "0 0 24px 6px #b3e5fc88, 0 0 0 2px #fff8",
                    animation: `bubble-float ${animationDuration}s ease-in-out infinite alternate`,
                }}
            >
                {/* Soap bubble highlight */}
                <div
                    style={{
                        position: "absolute",
                        left: "20%",
                        top: "18%",
                        width: "30%",
                        height: "28%",
                        borderRadius: "50%",
                        background: "rgba(255,255,255,0.35)",
                        filter: "blur(1.5px)",
                        pointerEvents: "none",
                    }}
                />
                {/* Splash droplets */}
                {popped &&
                    SPLASH_DIRECTIONS.map((dir, i) => (
                        <div
                            key={i}
                            className="bubble-splash-droplet"
                            style={{
                                "--dir-x": dir.x,
                                "--dir-y": dir.y,
                                "--bubble-size": `${size}px`,
                            } as React.CSSProperties}
                        />
                    ))}
            </div>
        </div>
    );
};

export default Bubble;
