import React, { useState, useEffect } from 'react';
import Bubble from './components/Bubble';

interface BubbleData {
    id: number;
    position: { x: number; y: number };
}

const UI_WIDTH = 260;
const UI_HEIGHT = 140;
const UPGRADE_COST = 50;
const STORAGE_KEY = 'bubble-clicker-game-state';

const getRandomPosition = () => {
    // UI panel is at left: 20, top: 20, width: 260, height: 140
    const minX = UI_WIDTH + 40; // 260 + 40 = 300, so bubbles start to the right of UI
    const minY = 0;
    const maxX = window.innerWidth - 80;
    const maxY = window.innerHeight - 120;
    return {
        x: Math.random() * (maxX - minX) + minX,
        y: Math.random() * (maxY - minY) + minY,
    };
};

const getInitialState = () => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
        try {
            const state = JSON.parse(saved);
            return {
                currency: state.currency ?? 0,
                upgradeLevel: state.upgradeLevel ?? 0,
            };
        } catch {
            // ignore
        }
    }
    return { currency: 0, upgradeLevel: 0 };
};

const initialState = getInitialState();
console.log('Loaded state:', initialState);

const App: React.FC = () => {
    const [bubbles, setBubbles] = useState<BubbleData[]>([]);
    const [currency, setCurrency] = useState<number>(initialState.currency);
    const [upgradeLevel, setUpgradeLevel] = useState<number>(initialState.upgradeLevel);
    const [shopOpen, setShopOpen] = useState(false);

    const goldPerSecond = upgradeLevel;

    // Save game state to localStorage every 5 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            const data = { currency, upgradeLevel };
            localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
        }, 5000);
        return () => clearInterval(interval);
    }, [currency, upgradeLevel]);

    // Spawn a bubble every second
    useEffect(() => {
        const interval = setInterval(() => {
            setBubbles(bubs => [
                ...bubs,
                { id: Date.now() + Math.random(), position: getRandomPosition() }
            ]);
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    // Gold per second effect
    useEffect(() => {
        if (goldPerSecond > 0) {
            const interval = setInterval(() => {
                setCurrency((c: number) => c + goldPerSecond);
            }, 1000);
            return () => clearInterval(interval);
        }
    }, [goldPerSecond]);

    const handlePop = (id: number) => {
        setBubbles(bubs => bubs.filter(b => b.id !== id));
        setCurrency((c: number) => c + 1);
    };

    const handleUpgrade = () => {
        if (currency >= UPGRADE_COST) {
            setCurrency((c: number) => c - UPGRADE_COST);
            setUpgradeLevel((lvl: number) => lvl + 1);
        }
    };

    // Example shop items
    const shopItems = [
        { id: 1, name: 'Bubble Blaster', cost: 100 },
        { id: 2, name: 'Auto Popper', cost: 250 },
        { id: 3, name: 'Golden Soap', cost: 500 },
    ];

    const handleBuy = (item: { id: number; name: string; cost: number }) => {
        if (currency >= item.cost) {
            setCurrency((c: number) => c - item.cost);
            // Add your item effect logic here
            alert(`You bought: ${item.name}`);
        }
    };

    return (
        <div className="relative w-screen h-screen overflow-hidden bg-gradient-to-b from-blue-100 to-white">
            {/* Shop Icon Button */}
            <button
                className="fixed top-6 left-6 z-40 bg-gradient-to-br from-blue-200 to-blue-400 rounded-full shadow-2xl p-3 text-2xl hover:scale-110 hover:bg-blue-300 transition-all border-2 border-white/70 backdrop-blur"
                onClick={() => setShopOpen(true)}
                aria-label="Open Shop"
            >
                <span role="img" aria-label="shop">🛒</span>
            </button>

            {/* Backdrop */}
            {shopOpen && (
                <div
                    className="fixed inset-0 bg-black/40 z-30 backdrop-blur-sm transition-opacity duration-300"
                    onClick={() => setShopOpen(false)}
                    aria-label="Close Shop"
                />
            )}

            {/* Side Shop Menu */}
            <div
                className={`fixed top-0 left-0 h-full w-[26rem] max-w-full z-50 flex flex-col transition-transform duration-300 ${
                    shopOpen ? 'translate-x-0' : '-translate-x-full'
                }`}
                style={{ borderTopRightRadius: '2rem', borderBottomRightRadius: '2rem' }}
                tabIndex={shopOpen ? 0 : -1}
                aria-hidden={!shopOpen}
            >
                <div className="flex items-center justify-between px-8 py-6 border-b bg-gradient-to-r from-blue-200/80 to-blue-100/80 rounded-tr-3xl shadow-lg">
                    <div className="flex items-center gap-3">
                        <span className="text-3xl">🛍️</span>
                        <h3 className="text-2xl font-bold text-blue-700 tracking-wide drop-shadow">Shop</h3>
                    </div>
                    <button
                        className="text-3xl font-bold text-gray-400 hover:text-blue-500 transition"
                        onClick={() => setShopOpen(false)}
                        aria-label="Close Shop"
                    >
                        ×
                    </button>
                </div>
                <div className="p-8 border-b bg-white/70 backdrop-blur rounded-br-3xl">
                    <div className="mb-3 font-semibold text-blue-700 text-lg flex items-center gap-2">
                        <svg width="22" height="22" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" stroke="#0288d1" strokeWidth="2" fill="#b3e5fc"/></svg>
                        Upgrade Level: <span className="text-blue-900">{upgradeLevel}</span>
                    </div>
                    <button
                        onClick={handleUpgrade}
                        disabled={currency < UPGRADE_COST}
                        className="w-full mb-6 px-4 py-2 rounded-lg bg-gradient-to-r from-blue-400 to-blue-500 text-white font-semibold shadow hover:from-blue-500 hover:to-blue-600 transition disabled:bg-gray-300 disabled:cursor-not-allowed"
                    >
                        Buy Upgrade <span className="font-normal text-base">(+1 gold/sec)</span> — {UPGRADE_COST} gold
                    </button>
                </div>
                <ul className="p-8 flex-1 overflow-y-auto bg-white/60 backdrop-blur rounded-br-3xl">
                    {shopItems.map(item => (
                        <li key={item.id} className="flex items-center justify-between mb-6">
                            <span className="font-medium text-lg">{item.name}</span>
                            <span className="mx-2 text-yellow-700 font-semibold">{item.cost}💰</span>
                            <button
                                className="px-4 py-1.5 rounded-lg bg-gradient-to-r from-blue-400 to-blue-500 text-white font-semibold shadow hover:from-blue-500 hover:to-blue-600 transition disabled:bg-gray-300 disabled:cursor-not-allowed"
                                disabled={currency < item.cost}
                                onClick={() => handleBuy(item)}
                            >
                                Buy
                            </button>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Gold and Gold Per Second Display */}
            <div className="fixed top-6 right-8 z-20 flex items-center gap-6 bg-white/80 rounded-xl shadow-lg px-6 py-3">
                <div className="flex items-center gap-2 text-2xl font-bold text-blue-900">
                    💰 {currency}
                </div>
                <div className="flex items-center gap-1 text-lg text-blue-600 font-semibold">
                    <svg width="20" height="20" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" stroke="#0288d1" strokeWidth="2" fill="#b3e5fc"/></svg>
                    {goldPerSecond} /s
                </div>
            </div>

            {/* Bubbles */}
            {bubbles.map(bubble => (
                <Bubble
                    key={bubble.id}
                    position={bubble.position}
                    onPop={() => handlePop(bubble.id)}
                />
            ))}
</div>
    );
};

export default App;
