import React, { useMemo, useState } from "react";

interface BubbleProps {
    position: { x: number }; // Only x is needed, y is always bottom
    onPop: () => void;
}

const Bubble: React.FC<BubbleProps> = ({ position, onPop }) => {
    const [popped, setPopped] = useState(false);

    // Randomize size and float duration
    const { size, floatDuration } = useMemo(() => {
        const size = 48 + Math.random() * 32;
        const floatDuration = 3.5 + Math.random() * 2.5; // 3.5-6s
        return { size, floatDuration };
    }, []);

    const handlePop = () => {
        setPopped(true);
        setTimeout(onPop, 400);
    };

    return (
        <div
            className="absolute cursor-pointer select-none"
            style={{
                left: position.x,
                bottom: 0,
                width: size,
                height: size,
                zIndex: 10,
                pointerEvents: popped ? "none" : "auto",
                animation: !popped
                    ? `bubble-float-up ${floatDuration}s linear forwards`
                    : undefined,
            }}
            onClick={handlePop}
        >
            <div className={`bubble-visual ${popped ? "bubble-pop" : ""}`}>
                <div className="bubble-highlight" />
                {/* Splash droplets if you want */}
            </div>
        </div>
    );
};

const spawnBubble = () => ({
    x: Math.random() * (window.innerWidth - 80), // 80 = bubble max size
});

export default Bubble;
